import axios from "axios";

const CustomerAPI = {
    sendOTP: async (email) => {
        try {
            const res = await axios.get(`/send-email?email=${email}`);

            if (res.status === 0) {
                return true;
            }

            return false;
        } catch (err) {
            console.log(err);
        }
    },
};

export default CustomerAPI;
