import { useState, useEffect } from "react";
import OtpInput from "react-otp-input";
import { Button, Modal } from "react-bootstrap";

import "./OTPModal.scss";

function OTPModal(props) {
    const [otp, setOtp] = useState("");
    const [phoneNumber, setPhoneNumber] = useState(0);
    const [stage, setStage] = useState(1);

    const { showModal, handleCloseModal } = props;

    const modalContent = {
        modalPhoneNumber: {
            title: "Đăng Nhập Hoặc Tạo Tài Khoản",
            text: "Nhập số điện thoại mua hàng để hưởng đặc quyền riêng tại FPT Shop",
            inputData: <input type="text" />,
        },
        modalOTP: {
            title: "Xác Thực OTP",
            text: `Mã OTP đã được gửi đến số điện thoại ${phoneNumber || 0}`,
            inputData: <input type="text" />,
        },
        modalUserInfo: {
            title: "",
            text: "Nhập số điện thoại mua hàng để hưởng đặc quyền riêng tại FPT Shop",
            inputData: <input type="text" />,
        },
    };

    return (
        <>
            {stage === 1 && (
                <Modal
                    show={showModal}
                    onHide={handleCloseModal}
                    aria-labelledby="contained-modal-title-vcenter"
                    centered
                >
                    <Modal.Header closeButton>
                        <Modal.Title>Xác Thực OTP</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <p className="text-center">Mã OTP đã được gửi đến số điện thoại {phoneNumber}</p>
                        <OtpInput
                            value={otp}
                            onChange={setOtp}
                            numInputs={6}
                            renderInput={(props) => <input {...props} />}
                            containerStyle={"container_modal"}
                            inputStyle={"otp_input"}
                            placeholder="..."
                        />
                    </Modal.Body>
                    <Modal.Footer style={{ justifyContent: "center" }}>
                        <Button variant="danger" onClick={handleCloseModal}>
                            Xác nhận
                        </Button>
                    </Modal.Footer>
                </Modal>
            )}
        </>
    );
}

export default OTPModal;
