import { useState } from "react";
import OtpInput from "react-otp-input";
import { Button, Modal, Form } from "react-bootstrap";

import img_login from "../../asset/imgs/img-login.png";
import img_otp from "../../asset/imgs/img-otp.png";
import "./LoginModal.scss";

function LoginModal(props) {
    const [otp, setOtp] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [modalStage, setModalStage] = useState("modalPhoneNumber");

    const { showModal, handleCloseModal } = props;

    const modalStages = ["modalPhoneNumber", "modalOTP", "modalUserInfo"];

    const modalContent = {
        modalPhoneNumber: (
            <>
                <Modal.Header closeButton>
                    <Modal.Title>Đăng Nhập Hoặc Tạo Tài Khoản</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <div className="img" style={{ backgroundImage: `url(${img_login})` }}></div>
                    <p className="text-center" style={{ padding: "0 100px" }}>
                        Nhập email mua hàng để hưởng đặc quyền riêng tại FPT Shop
                    </p>
                    <Form.Control
                        type="text"
                        className="phone_input"
                        placeholder="Nhập số điện thoại"
                        value={phoneNumber}
                        onChange={(e) => setPhoneNumber(e.target.value)}
                    />
                </Modal.Body>
                <Modal.Footer style={{ justifyContent: "center" }}>
                    <Button
                        variant="danger"
                        onClick={() => {
                            if (handleConfirmPhoneNumber(phoneNumber) && phoneNumber.length === 10)
                                handleSwitchModal(modalStages[1]);
                        }}
                    >
                        Xác nhận
                    </Button>
                </Modal.Footer>
            </>
        ),
        modalOTP: (
            <>
                <Modal.Header closeButton>
                    <Modal.Title>Xác Thực OTP</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <div className="img" style={{ backgroundImage: `url(${img_otp})` }}></div>
                    <p className="text-center">Mã OTP đã được gửi đến số điện thoại {phoneNumber || 0}</p>
                    <span className="text_special" onClick={() => setModalStage(modalStages[0])}>
                        <i className="fa-regular fa-pen-to-square"></i>
                        Đổi số điện thoại nhận mã
                    </span>
                    <OtpInput
                        value={otp}
                        onChange={setOtp}
                        numInputs={6}
                        renderInput={(props) => <input {...props} />}
                        containerStyle={"container_modal"}
                        inputStyle={"otp_input"}
                    />
                </Modal.Body>
                <Modal.Footer style={{ justifyContent: "center" }}>
                    <Button
                        variant="danger"
                        onClick={() => {
                            if (handleConfirmOTP()) setModalStage(modalStages[2]);
                        }}
                    >
                        Xác nhận
                    </Button>
                    <span className="text_special" onClick={() => handleConfirmOTP()}>
                        Gửi lại mã OTP cho tôi
                    </span>
                </Modal.Footer>
            </>
        ),
        modalUserInfo: (
            <>
                <Modal.Header closeButton>
                    <Modal.Title>Xác Thực OTP</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <p className="text-center">Mã OTP đã được gửi đến số điện thoại ${phoneNumber || 0}</p>
                    <OtpInput
                        value={otp}
                        onChange={setOtp}
                        numInputs={6}
                        renderInput={(props) => <input {...props} />}
                        containerStyle={"container_modal"}
                        inputStyle={"otp_input"}
                        placeholder="..."
                    />
                </Modal.Body>
                <Modal.Footer style={{ justifyContent: "center" }}>
                    <Button variant="danger" onClick={handleCloseModal}>
                        Xác nhận
                    </Button>
                </Modal.Footer>
            </>
        ),
    };

    const handleSwitchModal = (stage) => {
        setModalStage(stage);
    };

    const handleConfirmOTP = () => {
        return true;
    };

    const handleConfirmPhoneNumber = (phoneNumber) => {
        if (phoneNumber.length > 10) return false;

        return phoneNumber.split("").every((char) => +char === 0 || +char);
    };

    return (
        <>
            <Modal show={showModal} onHide={handleCloseModal} aria-labelledby="contained-modal-title-vcenter" centered>
                {modalContent[modalStage]}
            </Modal>
        </>
    );
}

export default LoginModal;
