import { useState } from "react";
import OtpInput from "react-otp-input";
import { Button, Modal } from "react-bootstrap";

import "./OTPModal.scss";

function OTPModal(props) {
    const [otp, setOtp] = useState("");
    const { showModal, handleCloseModal, phoneNumber } = props;

    console.log({ otp });
    return (
        <>
            <Modal show={showModal} onHide={handleCloseModal} aria-labelledby="contained-modal-title-vcenter" centered>
                <Modal.Header closeButton>
                    <Modal.Title>Xác Thực OTP</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <p className="text-center">Mã OTP đã được gửi đến số điện thoại {phoneNumber}</p>
                    <OtpInput
                        value={otp}
                        onChange={setOtp}
                        numInputs={6}
                        renderInput={(props) => <input {...props} />}
                        containerStyle={"container_modal"}
                        inputStyle={"otp_input"}
                    />
                </Modal.Body>
                <Modal.Footer className="text-center">
                    <Button variant="danger" onClick={handleCloseModal}>
                        Xác nhận
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default OTPModal;
