import { useState, useEffect } from "react";
import OtpInput from "react-otp-input";
import { Button, Modal } from "react-bootstrap";

import "./OTPModal.scss";

function OTPModal(props) {
    const [otp, setOtp] = useState("");
    const [phoneNumber, setPhoneNumber] = useState(0);
    const [modalStage, setModalStage] = useState("modalPhoneNumber");

    const { showModal, handleCloseModal } = props;

    const modalStages = ["modalPhoneNumber", "modalOTP", "modalUserInfo"];

    const modalContent = {
        modalPhoneNumber: {
            title: "Đăng Nhập Hoặc Tạo Tài Khoản",
            text: "Nhập số điện thoại mua hàng để hưởng đặc quyền riêng tại FPT Shop",
            inputData: <input type="text" />,
        },
        modalOTP: {
            title: "Xác Thực OTP",
            text: `Mã OTP đã được gửi đến số điện thoại ${phoneNumber || 0}`,
            inputData: (
                <OtpInput
                    value={otp}
                    onChange={setOtp}
                    numInputs={6}
                    renderInput={(props) => <input {...props} />}
                    containerStyle={"container_modal"}
                    inputStyle={"otp_input"}
                    placeholder="..."
                />
            ),
        },
        modalUserInfo: {
            title: "",
            text: "Nhập số điện thoại mua hàng để hưởng đặc quyền riêng tại FPT Shop",
            inputData: <input type="text" />,
        },
    };

    return (
        <>
            <Modal show={showModal} onHide={handleCloseModal} aria-labelledby="contained-modal-title-vcenter" centered>
                <Modal.Header closeButton>
                    <Modal.Title>{modalContent[modalStage].title}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <p className="text-center">{modalContent[modalStage].text}</p>
                    {modalContent[modalStage].inputData}
                </Modal.Body>
                <Modal.Footer style={{ justifyContent: "center" }}>
                    <Button variant="danger" onClick={handleCloseModal}>
                        Xác nhận
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default OTPModal;
