import axios from "../axios.js";

const AuthAPI = {
    login: async () => {
        try {
            const res = await axios.get(`/user?email=${email}&limit=1`);

            if (res.status === 0) {
                return res.data;
            }

            return false;
        } catch (err) {
            console.log(err);
            return false;
        }
    },
};

export default AuthAPI;
